const upBtn=document.querySelector(".up-button"),downBtn=document.querySelector(".down-button"),sidebar=document.querySelector(".sidebar"),container=document.querySelector(".container"),mainSlide=document.querySelector(".main-slide"),slidesCount=mainSlide.querySelectorAll("div").length;let activeSlideIndex=0;function changesSlide(e){"up"===e?(activeSlideIndex++,activeSlideIndex===slidesCount&&(activeSlideIndex=0)):"down"===e&&(activeSlideIndex--,activeSlideIndex<0&&(activeSlideIndex=slidesCount-1));const n=container.clientHeight;mainSlide.style.transform=`translateY(-${activeSlideIndex*n}px)`,sidebar.style.transform=`translateY(${activeSlideIndex*n}px)`}sidebar.style.top=`-${100*(slidesCount-1)}vh`,upBtn.addEventListener("click",()=>{changesSlide("up")}),downBtn.addEventListener("click",()=>{changesSlide("down")});